mapper.select:
select 'false' as QUERYID,

from abas.CuadroComparativoFuente A1

where A1.IdCuadroComparativoFuente = #{idcuadrocomparativofuente,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.CuadroComparativoFuente A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoTipoFuente = A3.vchregCodigo LEFT JOIN abas.gentabla A5 ON A1.IdCatalogoTipoBien = A5.vchregCodigo LEFT JOIN abas.gentabla A7 ON A1.IdCatalogoTipoFuente = A7.vchregCodigo LEFT JOIN abas.gentabla A9 ON A1.IdCatalogoTipoBien = A9.vchregCodigo LEFT JOIN abas.gentabla A11 ON A1.IdCatalogoMonedaFuente = A11.vchregCodigo LEFT JOIN abas.PacConsolidado A13 ON A1.IdPacConsolidado = A13.IdPacConsolidado where A1.IdCuadroComparativoFuente = #{idcuadrocomparativofuente,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.CuadroComparativoFuente A1


		 AND A1.IDCUADROCOMPARATIVOFUENTE = #{idcuadrocomparativofuente,jdbcType=INTEGER} 


		 AND A1.IDPACCONSOLIDADO = #{idpacconsolidado,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOTIPOFUENTE = #{idcatalogotipofuente,jdbcType=VARCHAR} 


		 AND A1.IDCATALOGOTIPOBIEN = #{idcatalogotipobien,jdbcType=VARCHAR} 


		 AND A1.PROVEEDOR  >=  #{proveedorini,jdbcType=DECIMAL} 



		 AND A1.PROVEEDOR  <=   #{proveedorfin,jdbcType=DECIMAL} 



		 AND A1.RUCPROVEEDOR LIKE ('%' + #{rucproveedor,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREPROVEEDOR LIKE ('%' + #{nombreproveedor,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBRECONTACTO LIKE ('%' + #{nombrecontacto,jdbcType=VARCHAR} + '%')


		 AND A1.TELEFONOPROVEEDOR LIKE ('%' + #{telefonoproveedor,jdbcType=VARCHAR} + '%')


		 AND A1.CORREOPROVEEDOR LIKE ('%' + #{correoproveedor,jdbcType=VARCHAR} + '%')


		 AND A1.ENTIDADCONVOCANTE LIKE ('%' + #{entidadconvocante,jdbcType=VARCHAR} + '%')


		 AND A1.TIPONUMEROPROCESO LIKE ('%' + #{tiponumeroproceso,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACONSENTIMIENTOBUENAPRO)   >=   #{fechaconsentimientobuenaproini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACONSENTIMIENTOBUENAPRO)  <=   #{fechaconsentimientobuenaprofin,jdbcType=TIMESTAMP} 



		 AND A1.MARCA LIKE ('%' + #{marca,jdbcType=VARCHAR} + '%')


		 AND A1.MODELO LIKE ('%' + #{modelo,jdbcType=VARCHAR} + '%')


		 AND A1.PROCEDENCIA LIKE ('%' + #{procedencia,jdbcType=VARCHAR} + '%')


		 AND A1.ANIOFABRICACION LIKE ('%' + #{aniofabricacion,jdbcType=VARCHAR} + '%')


		 AND A1.GARANTIACOMERCIAL LIKE ('%' + #{garantiacomercial,jdbcType=VARCHAR} + '%')


		 AND A1.PLAZOENTREGA = #{plazoentrega,jdbcType=INTEGER} 


		 AND A1.FORMAPAGO LIKE ('%' + #{formapago,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOMONEDAFUENTE = #{idcatalogomonedafuente,jdbcType=VARCHAR} 


		 AND A1.PRECIOUNITARIOMONEDACONSIGNADA LIKE ('%' + #{preciounitariomonedaconsignada,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOCAMBIO  >=  #{tipocambioini,jdbcType=DECIMAL} 



		 AND A1.TIPOCAMBIO  <=   #{tipocambiofin,jdbcType=DECIMAL} 



		 AND  (A1.FECHASOLICITUD)   >=   #{fechasolicitudini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHASOLICITUD)  <=   #{fechasolicitudfin,jdbcType=TIMESTAMP} 



		 AND A1.VECESREITEROSOLICITUD = #{vecesreiterosolicitud,jdbcType=INTEGER} 


		 AND  (A1.FECHARECEPCION)   >=   #{fecharecepcionini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHARECEPCION)  <=   #{fecharecepcionfin,jdbcType=TIMESTAMP} 



		 AND A1.PROVEEDORDEDICACONTRATACION LIKE ('%' + #{proveedordedicacontratacion,jdbcType=VARCHAR} + '%')


		 AND A1.USUARIOPARTICIPORTM LIKE ('%' + #{usuarioparticiportm,jdbcType=VARCHAR} + '%')


		 AND A1.CUMPLERTM LIKE ('%' + #{cumplertm,jdbcType=VARCHAR} + '%')


		 AND A1.SETOMOENCUENTA LIKE ('%' + #{setomoencuenta,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPACCONSOLIDADO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOTIPOFUENTE IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOTIPOBIEN IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOMONEDAFUENTE IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

