mapper.select:
select 'false' as QUERYID,

from abas.CuadroComparativoVR A1

where A1.IdCuadroComparativoVR = #{idcuadrocomparativovr,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.CuadroComparativoVR A1
LEFT JOIN abas.CuadroComparativoItem A3 ON A1.IdCuadroComparativoItem = A3.IdCuadroComparativoItem LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoProcedimientoVR = A6.vchregCodigo where A1.IdCuadroComparativoVR = #{idcuadrocomparativovr,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.CuadroComparativoVR A1


		 AND A1.IDCUADROCOMPARATIVOVR = #{idcuadrocomparativovr,jdbcType=INTEGER} 


		 AND A1.IDCUADROCOMPARATIVOITEM = #{idcuadrocomparativoitem,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOPROCEDIMIENTOVR = #{idcatalogoprocedimientovr,jdbcType=VARCHAR} 


		 AND A1.VALORREFERENCIALITEM  >=  #{valorreferencialitemini,jdbcType=DECIMAL} 



		 AND A1.VALORREFERENCIALITEM  <=   #{valorreferencialitemfin,jdbcType=DECIMAL} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCUADROCOMPARATIVOITEM IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOPROCEDIMIENTOVR IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.CuadroComparativoVR A1
LEFT JOIN abas.CuadroComparativoItem A3 ON A1.IdCuadroComparativoItem = A3.IdCuadroComparativoItem
LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoProcedimientoVR = A6.vchregCodigo


		 AND A1.IDCUADROCOMPARATIVOVR = #{idcuadrocomparativovr,jdbcType=INTEGER} 


		 AND A1.IDCUADROCOMPARATIVOITEM = #{idcuadrocomparativoitem,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOPROCEDIMIENTOVR = #{idcatalogoprocedimientovr,jdbcType=VARCHAR} 


		 AND A1.VALORREFERENCIALITEM  >=  #{valorreferencialitemini,jdbcType=DECIMAL} 



		 AND A1.VALORREFERENCIALITEM  <=   #{valorreferencialitemfin,jdbcType=DECIMAL} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCUADROCOMPARATIVOITEM IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOPROCEDIMIENTOVR IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}
