) 
VALUES (
#{iddependenciadocumentotecnico,jdbcType=INTEGER}, #{iddocumentotecnico,jdbcType=INTEGER}, #{direccion,jdbcType=VARCHAR}, #{fechacreacionauditoria,jdbcType=TIMESTAMP}, 
#{usuariocreacionauditoria,jdbcType=VARCHAR}, #{fechamodificacionauditoria,jdbcType=TIMESTAMP}, #{usuariomodificacionauditoria,jdbcType=VARCHAR}, #{estadoauditoria,jdbcType=VARCHAR} 
