mapper.select:
select 'false' as QUERYID,

from abas.DetallePedido A1

where A1.IdDetallePedido = #{iddetallepedido,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.DetallePedido A1
LEFT JOIN abas.Pedido A3 ON A1.IdPedido = A3.IdPedido where A1.IdDetallePedido = #{iddetallepedido,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.DetallePedido A1


		 AND A1.IDDETALLEPEDIDO = #{iddetallepedido,jdbcType=INTEGER} 


		 AND A1.IDPEDIDO = #{idpedido,jdbcType=INTEGER} 


		 AND A1.GRUPOBIEN LIKE ('%' + #{grupobien,jdbcType=VARCHAR} + '%')


		 AND A1.CLASEBIEN LIKE ('%' + #{clasebien,jdbcType=VARCHAR} + '%')


		 AND A1.FAMILIABIEN LIKE ('%' + #{familiabien,jdbcType=VARCHAR} + '%')


		 AND A1.ITEMBIEN LIKE ('%' + #{itembien,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREITEM LIKE ('%' + #{nombreitem,jdbcType=VARCHAR} + '%')


		 AND A1.UNIDADMEDIDA = #{unidadmedida,jdbcType=INTEGER} 


		 AND A1.CANTIDADAPROBADASIGA = #{cantidadaprobadasiga,jdbcType=INTEGER} 


		 AND A1.CANTIDADSOLICITADASIGA = #{cantidadsolicitadasiga,jdbcType=INTEGER} 


		 AND A1.CANTIDADATENDIDASIGA = #{cantidadatendidasiga,jdbcType=INTEGER} 


		 AND A1.PRECIOUNITARIO  >=  #{preciounitarioini,jdbcType=DECIMAL} 



		 AND A1.PRECIOUNITARIO  <=   #{preciounitariofin,jdbcType=DECIMAL} 



		 AND A1.VALORTOTAL  >=  #{valortotalini,jdbcType=DECIMAL} 



		 AND A1.VALORTOTAL  <=   #{valortotalfin,jdbcType=DECIMAL} 



		 AND A1.ESTADOPEDIDOSIGA = #{estadopedidosiga,jdbcType=SMALLINT} 


		 AND A1.CODIGOCLASIFICADOR LIKE ('%' + #{codigoclasificador,jdbcType=VARCHAR} + '%')


		 AND A1.CODIGOTAREASIGA LIKE ('%' + #{codigotareasiga,jdbcType=VARCHAR} + '%')


		 AND A1.CODIGOTAREAPLANIN LIKE ('%' + #{codigotareaplanin,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.CLASIFICADOR LIKE ('%' + #{clasificador,jdbcType=VARCHAR} + '%')


		 AND A1.OBSERVADOPORCP = #{observadoporcp,jdbcType=SMALLINT} 


		 AND  (A1.FECHAOBSERVADOPORCP)   >=   #{fechaobservadoporcpini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAOBSERVADOPORCP)  <=   #{fechaobservadoporcpfin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHARETIRADOPORCP)   >=   #{fecharetiradoporcpini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHARETIRADOPORCP)  <=   #{fecharetiradoporcpfin,jdbcType=TIMESTAMP} 



		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPEDIDO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

