select 'false' as QUERYID,

from abas.Entregable A1

where A1.IdEntregable = #{identregable,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.Entregable A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoEstadoEntregable = A3.vchregCodigo LEFT JOIN abas.GrupoDocumento A5 ON A1.IdGrupoDocumento = A5.IdGrupoDocumento LEFT JOIN abas.Orden A6 ON A1.IdOrden = A6.IdOrden where A1.IdEntregable = #{identregable,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.Entregable A1


		 AND A1.IDENTREGABLE = #{identregable,jdbcType=INTEGER} 


		 AND A1.IDORDEN = #{idorden,jdbcType=INTEGER} 


		 AND A1.NROPROVEIDO LIKE ('%' + #{nroproveido,jdbcType=VARCHAR} + '%')


		 AND A1.NROENTREGABLE LIKE ('%' + #{nroentregable,jdbcType=VARCHAR} + '%')


		 AND A1.PLAZOENTREGABLE LIKE ('%' + #{plazoentregable,jdbcType=VARCHAR} + '%')


		 AND A1.MONTOENTREGABLE  >=  #{montoentregableini,jdbcType=DECIMAL} 



		 AND A1.MONTOENTREGABLE  <=   #{montoentregablefin,jdbcType=DECIMAL} 



		 AND A1.MONTOPENALIDADENTREGABLE  >=  #{montopenalidadentregableini,jdbcType=DECIMAL} 



		 AND A1.MONTOPENALIDADENTREGABLE  <=   #{montopenalidadentregablefin,jdbcType=DECIMAL} 



		 AND  (A1.FECHAPRESENTACIONENTREGABLE)   >=   #{fechapresentacionentregableini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAPRESENTACIONENTREGABLE)  <=   #{fechapresentacionentregablefin,jdbcType=TIMESTAMP} 



		 AND A1.OBSERVACIONESENTREGABLE LIKE ('%' + #{observacionesentregable,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOESTADOENTREGABLE = #{idcatalogoestadoentregable,jdbcType=VARCHAR} 


		 AND A1.IDGRUPODOCUMENTO = #{idgrupodocumento,jdbcType=INTEGER} 


		 AND A1.ANIO = #{anio,jdbcType=INTEGER} 


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDORDEN IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOESTADOENTREGABLE IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDGRUPODOCUMENTO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.Entregable A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoEstadoEntregable = A3.vchregCodigo
LEFT JOIN abas.GrupoDocumento A5 ON A1.IdGrupoDocumento = A5.IdGrupoDocumento
LEFT JOIN abas.Orden A6 ON A1.IdOrden = A6.IdOrden


		 AND A1.IDENTREGABLE = #{identregable,jdbcType=INTEGER} 


