mapper.select:
select 'false' as QUERYID,

from abas.ItinerarioConvenioMarco A1

where A1.IdItinerarioConvenioMarco = #{iditinerarioconveniomarco,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.ItinerarioConvenioMarco A1
LEFT JOIN abas.DocumentoTecnico A3 ON A1.IdDocumentoTecnico = A3.IdDocumentoTecnico where A1.IdItinerarioConvenioMarco = #{iditinerarioconveniomarco,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.ItinerarioConvenioMarco A1


		 AND A1.IDITINERARIOCONVENIOMARCO = #{iditinerarioconveniomarco,jdbcType=INTEGER} 


		 AND A1.IDDOCUMENTOTECNICO = #{iddocumentotecnico,jdbcType=INTEGER} 


		 AND A1.TIPOITINERARIO LIKE ('%' + #{tipoitinerario,jdbcType=VARCHAR} + '%')


		 AND A1.CIUDADORIGEN LIKE ('%' + #{ciudadorigen,jdbcType=VARCHAR} + '%')


		 AND A1.FECHASALIDA  >=  #{fechasalidaini,jdbcType=DATE} 



		 AND A1.FECHASALIDA  <=   #{fechasalidafin,jdbcType=DATE} 



		 AND  (A1.HORASALIDA)   >=   #{horasalidaini,jdbcType=TIMESTAMP} 



		 AND (A1.HORASALIDA)  <=   #{horasalidafin,jdbcType=TIMESTAMP} 



		 AND A1.CIUDADDESTINO LIKE ('%' + #{ciudaddestino,jdbcType=VARCHAR} + '%')


		 AND A1.FECHARETERNO  >=  #{fechareternoini,jdbcType=DATE} 



		 AND A1.FECHARETERNO  <=   #{fechareternofin,jdbcType=DATE} 



		 AND  (A1.HORARETORNO)   >=   #{horaretornoini,jdbcType=TIMESTAMP} 



		 AND (A1.HORARETORNO)  <=   #{horaretornofin,jdbcType=TIMESTAMP} 



		 AND A1.DNI LIKE ('%' + #{dni,jdbcType=VARCHAR} + '%')


		 AND A1.APELLIDOPATERNO LIKE ('%' + #{apellidopaterno,jdbcType=VARCHAR} + '%')


		 AND A1.APELLIDOMATERNO LIKE ('%' + #{apellidomaterno,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBERS LIKE ('%' + #{nombers,jdbcType=VARCHAR} + '%')


		 AND A1.CORREO LIKE ('%' + #{correo,jdbcType=VARCHAR} + '%')


		 AND A1.TELEFONO LIKE ('%' + #{telefono,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOCONTRATOPERSONAL LIKE ('%' + #{tipocontratopersonal,jdbcType=VARCHAR} + '%')


		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDDOCUMENTOTECNICO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

