mapper.select:
select 'false' as QUERYID,

from abas.PrevisionPresupuestal A1

where A1.IdPrevisionPresupuestal = #{idprevisionpresupuestal,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.PrevisionPresupuestal A1
LEFT JOIN abas.PacConsolidado A3 ON A1.IdPacConsolidado = A3.IdPacConsolidado where A1.IdPrevisionPresupuestal = #{idprevisionpresupuestal,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.PrevisionPresupuestal A1


		 AND A1.IDPREVISIONPRESUPUESTAL = #{idprevisionpresupuestal,jdbcType=INTEGER} 


		 AND A1.IDPACCONSOLIDADO = #{idpacconsolidado,jdbcType=INTEGER} 


		 AND A1.NROPREVISION LIKE ('%' + #{nroprevision,jdbcType=VARCHAR} + '%')


		 AND A1.NRODOCUMENTOPPTO LIKE ('%' + #{nrodocumentoppto,jdbcType=VARCHAR} + '%')


		 AND A1.NRODOCUMENTOOGA LIKE ('%' + #{nrodocumentooga,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAAUTORIZACION)   >=   #{fechaautorizacionini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAAUTORIZACION)  <=   #{fechaautorizacionfin,jdbcType=TIMESTAMP} 



		 AND A1.ANIO LIKE ('%' + #{anio,jdbcType=VARCHAR} + '%')


		 AND A1.MONTOPREVISION  >=  #{montoprevisionini,jdbcType=DECIMAL} 



		 AND A1.MONTOPREVISION  <=   #{montoprevisionfin,jdbcType=DECIMAL} 



		 AND A1.MONTOPREVISIONCONTRATO  >=  #{montoprevisioncontratoini,jdbcType=DECIMAL} 



		 AND A1.MONTOPREVISIONCONTRATO  <=   #{montoprevisioncontratofin,jdbcType=DECIMAL} 



		 AND A1.MONTOAMPLIACION  >=  #{montoampliacionini,jdbcType=DECIMAL} 



		 AND A1.MONTOAMPLIACION  <=   #{montoampliacionfin,jdbcType=DECIMAL} 



		 AND A1.MONTOANULACION  >=  #{montoanulacionini,jdbcType=DECIMAL} 



		 AND A1.MONTOANULACION  <=   #{montoanulacionfin,jdbcType=DECIMAL} 



		 AND A1.FECHACREACIONAUDITORIA  >=  #{fechacreacionauditoriaini,jdbcType=DATE} 



		 AND A1.FECHACREACIONAUDITORIA  <=   #{fechacreacionauditoriafin,jdbcType=DATE} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.FECHAMODIFICACIONAUDITORIA  >=  #{fechamodificacionauditoriaini,jdbcType=DATE} 



		 AND A1.FECHAMODIFICACIONAUDITORIA  <=   #{fechamodificacionauditoriafin,jdbcType=DATE} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPACCONSOLIDADO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.PrevisionPresupuestal A1
LEFT JOIN abas.PacConsolidado A3 ON A1.IdPacConsolidado = A3.IdPacConsolidado


		 AND A1.IDPREVISIONPRESUPUESTAL = #{idprevisionpresupuestal,jdbcType=INTEGER} 


		 AND A1.IDPACCONSOLIDADO = #{idpacconsolidado,jdbcType=INTEGER} 


