		 AND A1.MONTOPREVISIONCONTRATO  >=  #{montoprevisioncontratoini,jdbcType=DECIMAL} 



		 AND A1.MONTOPREVISIONCONTRATO  <=   #{montoprevisioncontratofin,jdbcType=DECIMAL} 



