		 AND A1.MONTOAMPLIACION  <=   #{montoampliacionfin,jdbcType=DECIMAL} 



		 AND A1.MONTOANULACION  >=  #{montoanulacionini,jdbcType=DECIMAL} 



		 AND A1.MONTOANULACION  <=   #{montoanulacionfin,jdbcType=DECIMAL} 



