mapper.select:
select 'false' as QUERYID,

from abas.RequisitosConformidad A1

where A1.IdRequisitoConformidad = #{idrequisitoconformidad,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.RequisitosConformidad A1
LEFT JOIN abas.PacConsolidado A3 ON A1.IdPacConsolidado = A3.IdPacConsolidado where A1.IdRequisitoConformidad = #{idrequisitoconformidad,jdbcType=INTEGER}

mapper.insert:
insert into abas.RequisitosConformidad (
IDREQUISITOCONFORMIDAD, IDPACCONSOLIDADO, IDCATALOGOTIPODOCUMENTO, FECHADOCUMENTO, 
RUTAARCHIVO, FECHACREACIONAUDITORIA, USUARIOCREACIONAUDITORIA, FECHAMODIFICACIONAUDITORIA, 
USUARIOMODIFICACIONAUDITORIA, EQUIPOAUDITORIA, PROGRAMAAUDITORIA, ESTADOAUDITORIA 
) 
VALUES (
#{idrequisitoconformidad,jdbcType=INTEGER}, #{idpacconsolidado,jdbcType=INTEGER}, #{idcatalogotipodocumento,jdbcType=INTEGER}, #{fechadocumento,jdbcType=TIMESTAMP}, 
#{rutaarchivo,jdbcType=VARCHAR}, #{fechacreacionauditoria,jdbcType=TIMESTAMP}, #{usuariocreacionauditoria,jdbcType=VARCHAR}, #{fechamodificacionauditoria,jdbcType=TIMESTAMP}, 
#{usuariomodificacionauditoria,jdbcType=VARCHAR}, #{equipoauditoria,jdbcType=VARCHAR}, #{programaauditoria,jdbcType=VARCHAR}, #{estadoauditoria,jdbcType=VARCHAR} 
)

mapper.select:
select 'false' as QUERYID,

from abas.RequisitosConformidad A1


		 AND A1.IDREQUISITOCONFORMIDAD = #{idrequisitoconformidad,jdbcType=INTEGER} 


		 AND A1.IDPACCONSOLIDADO = #{idpacconsolidado,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOTIPODOCUMENTO = #{idcatalogotipodocumento,jdbcType=INTEGER} 


		 AND  (A1.FECHADOCUMENTO)   >=   #{fechadocumentoini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHADOCUMENTO)  <=   #{fechadocumentofin,jdbcType=TIMESTAMP} 



		 AND A1.RUTAARCHIVO LIKE ('%' + #{rutaarchivo,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPACCONSOLIDADO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

