mapper.select:
select 'false' as QUERYID,

from abas.SiafExpedienteSecuencia A1

where A1.IdSiafExpedienteSecuencia = #{idsiafexpedientesecuencia,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.SiafExpedienteSecuencia A1
LEFT JOIN abas.SiafLog A3 ON A1.IdLog = A3.IdLog LEFT JOIN abas.VCertificacion A4 ON A1.IdVCertificacion = A4.VCertificacion where A1.IdSiafExpedienteSecuencia = #{idsiafexpedientesecuencia,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.SiafExpedienteSecuencia A1


		 AND A1.IDSIAFEXPEDIENTESECUENCIA = #{idsiafexpedientesecuencia,jdbcType=INTEGER} 


		 AND A1.IDLOG = #{idlog,jdbcType=INTEGER} 


		 AND A1.FECHACARGA = #{fechacarga,jdbcType=INTEGER} 


		 AND A1.ANIO LIKE ('%' + #{anio,jdbcType=VARCHAR} + '%')


		 AND A1.SECEJEC LIKE ('%' + #{secejec,jdbcType=VARCHAR} + '%')


		 AND A1.EXPEDIENTE LIKE ('%' + #{expediente,jdbcType=VARCHAR} + '%')


		 AND A1.ANIOPROCESO LIKE ('%' + #{anioproceso,jdbcType=VARCHAR} + '%')


		 AND A1.CODIGODOCUMENTO LIKE ('%' + #{codigodocumento,jdbcType=VARCHAR} + '%')


		 AND A1.NUMERODOCUMENTO LIKE ('%' + #{numerodocumento,jdbcType=VARCHAR} + '%')


		 AND A1.MONTONACIONAL  >=  #{montonacionalini,jdbcType=DECIMAL} 



		 AND A1.MONTONACIONAL  <=   #{montonacionalfin,jdbcType=DECIMAL} 



		 AND A1.MESPROCESO LIKE ('%' + #{mesproceso,jdbcType=VARCHAR} + '%')


		 AND A1.FECHADOCUMENTO  >=  #{fechadocumentoini,jdbcType=DATE} 



		 AND A1.FECHADOCUMENTO  <=   #{fechadocumentofin,jdbcType=DATE} 



		 AND A1.CICLO LIKE ('%' + #{ciclo,jdbcType=VARCHAR} + '%')


		 AND A1.FASE LIKE ('%' + #{fase,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.IDVCERTIFICACION = #{idvcertificacion,jdbcType=INTEGER} 


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDLOG IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDVCERTIFICACION IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

