mapper.select:
select 'false' as QUERYID,

from abas.VDetallePedido A1

where A1.IdVDetallePedido = #{idvdetallepedido,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.VDetallePedido A1
LEFT JOIN abas.visPedido A3 ON A1.IdVPedido = A3.IdVPedido where A1.IdVDetallePedido = #{idvdetallepedido,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.VDetallePedido A1


		 AND A1.IDVDETALLEPEDIDO = #{idvdetallepedido,jdbcType=INTEGER} 


		 AND A1.IDVPEDIDO = #{idvpedido,jdbcType=INTEGER} 


		 AND A1.CODIGOUNIDADEJECUTORA LIKE ('%' + #{codigounidadejecutora,jdbcType=VARCHAR} + '%')


		 AND A1.ANIO  >=  #{anioini,jdbcType=DECIMAL} 



		 AND A1.ANIO  <=   #{aniofin,jdbcType=DECIMAL} 



		 AND A1.SECEJEC  >=  #{secejecini,jdbcType=DECIMAL} 



		 AND A1.SECEJEC  <=   #{secejecfin,jdbcType=DECIMAL} 



		 AND A1.NROCUADRO  >=  #{nrocuadroini,jdbcType=DECIMAL} 



		 AND A1.NROCUADRO  <=   #{nrocuadrofin,jdbcType=DECIMAL} 



		 AND A1.CENTROCOSTO LIKE ('%' + #{centrocosto,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOBIEN LIKE ('%' + #{tipobien,jdbcType=VARCHAR} + '%')


		 AND A1.NROPEDIDO LIKE ('%' + #{nropedido,jdbcType=VARCHAR} + '%')


		 AND A1.GRUPOBIEN LIKE ('%' + #{grupobien,jdbcType=VARCHAR} + '%')


		 AND A1.CLASEBIEN LIKE ('%' + #{clasebien,jdbcType=VARCHAR} + '%')


		 AND A1.FAMILIABIEN LIKE ('%' + #{familiabien,jdbcType=VARCHAR} + '%')


		 AND A1.ITEMBIEN LIKE ('%' + #{itembien,jdbcType=VARCHAR} + '%')


		 AND A1.UNIDADMEDIDA  >=  #{unidadmedidaini,jdbcType=DECIMAL} 



		 AND A1.UNIDADMEDIDA  <=   #{unidadmedidafin,jdbcType=DECIMAL} 



		 AND A1.NOMBREITEM LIKE ('%' + #{nombreitem,jdbcType=VARCHAR} + '%')


		 AND A1.CANTIDADSOLICITADA  >=  #{cantidadsolicitadaini,jdbcType=DECIMAL} 



		 AND A1.CANTIDADSOLICITADA  <=   #{cantidadsolicitadafin,jdbcType=DECIMAL} 



		 AND A1.CANTIDADAPROBADA  >=  #{cantidadaprobadaini,jdbcType=DECIMAL} 



		 AND A1.CANTIDADAPROBADA  <=   #{cantidadaprobadafin,jdbcType=DECIMAL} 



		 AND A1.CANTIDADATENDIDA  >=  #{cantidadatendidaini,jdbcType=DECIMAL} 



		 AND A1.CANTIDADATENDIDA  <=   #{cantidadatendidafin,jdbcType=DECIMAL} 



		 AND A1.PRECIOUNITARIO  >=  #{preciounitarioini,jdbcType=DECIMAL} 



		 AND A1.PRECIOUNITARIO  <=   #{preciounitariofin,jdbcType=DECIMAL} 



		 AND A1.VALORTOTAL  >=  #{valortotalini,jdbcType=DECIMAL} 



		 AND A1.VALORTOTAL  <=   #{valortotalfin,jdbcType=DECIMAL} 



		 AND A1.TIPOTAREA LIKE ('%' + #{tipotarea,jdbcType=VARCHAR} + '%')


		 AND A1.NIVELTAREA LIKE ('%' + #{niveltarea,jdbcType=VARCHAR} + '%')


		 AND A1.CODIGOTAREA  >=  #{codigotareaini,jdbcType=DECIMAL} 



		 AND A1.CODIGOTAREA  <=   #{codigotareafin,jdbcType=DECIMAL} 



		 AND A1.ESTADOPENDIENTE LIKE ('%' + #{estadopendiente,jdbcType=VARCHAR} + '%')


		 AND A1.IDCLASIFICADOR LIKE ('%' + #{idclasificador,jdbcType=VARCHAR} + '%')


		 AND A1.CLASIFICADOR LIKE ('%' + #{clasificador,jdbcType=VARCHAR} + '%')


		 AND A1.CODIGOTIPOBIEN = #{codigotipobien,jdbcType=INTEGER} 


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDVPEDIDO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

