mapper.select:
select 'false' as QUERYID,

from abas.CalendarioProcesoSeleccion A1

where A1.IdCalendarioProceso = #{idcalendarioproceso,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.CalendarioProcesoSeleccion A1
LEFT JOIN abas.ConvocatoriaProcesoSeleccion A3 ON A1.IdConvocatoriaProceso = A3.IdConvocatoriaProceso LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoCodigoCalendario = A6.vchregCodigo LEFT JOIN abas.gentabla A8 ON A1.IdCatalogoEstadoPublicacion = A8.vchregCodigo where A1.IdCalendarioProceso = #{idcalendarioproceso,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.CalendarioProcesoSeleccion A1


		 AND A1.IDCALENDARIOPROCESO = #{idcalendarioproceso,jdbcType=INTEGER} 


		 AND A1.IDCONVOCATORIAPROCESO = #{idconvocatoriaproceso,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOCODIGOCALENDARIO = #{idcatalogocodigocalendario,jdbcType=VARCHAR} 


		 AND A1.NOMBRECALENDARIO LIKE ('%' + #{nombrecalendario,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAINICIO)   >=   #{fechainicioini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAINICIO)  <=   #{fechainiciofin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHAFIN)   >=   #{fechafinini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAFIN)  <=   #{fechafinfin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHPUBLICACION)   >=   #{fechpublicacionini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHPUBLICACION)  <=   #{fechpublicacionfin,jdbcType=TIMESTAMP} 



		 AND A1.IDCATALOGOESTADOPUBLICACION = #{idcatalogoestadopublicacion,jdbcType=VARCHAR} 


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCONVOCATORIAPROCESO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOCODIGOCALENDARIO IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOESTADOPUBLICACION IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.CalendarioProcesoSeleccion A1
LEFT JOIN abas.ConvocatoriaProcesoSeleccion A3 ON A1.IdConvocatoriaProceso = A3.IdConvocatoriaProceso
LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoCodigoCalendario = A6.vchregCodigo
LEFT JOIN abas.gentabla A8 ON A1.IdCatalogoEstadoPublicacion = A8.vchregCodigo


		 AND A1.IDCALENDARIOPROCESO = #{idcalendarioproceso,jdbcType=INTEGER} 


