mapper.select:
select 'false' as QUERYID,

from abas.Contrato A1

where A1.IdContrato = #{idcontrato,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.Contrato A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoSistemaAdquisicion = A3.vchregCodigo LEFT JOIN abas.gentabla A5 ON A1.IdCatalogoTipoContrato = A5.vchregCodigo LEFT JOIN abas.gentabla A7 ON A1.IdCatalogoTipoBien = A7.vchregCodigo LEFT JOIN abas.gentabla A9 ON A1.IdCatalogoEstadoDocumentacion = A9.vchregCodigo LEFT JOIN abas.GrupoDocumento A11 ON A1.IdGrupoDocumento = A11.IdGrupoDocumento LEFT JOIN abas.UnidadEjecutora A12 ON A1.IdUnidadEjecutora = A12.IdUnidadEjecutora where A1.IdContrato = #{idcontrato,jdbcType=INTEGER}

mapper.insert:
	insert into abas.Contrato (IDCONTRATO,
	IDGRUPODOCUMENTO, DNIESPECIALISTAEJECUCION, NOMBREESPECIALISTA, FECHARECEPCIONEXPEDIENTE,
	NROCONSOLID, NROCONTRATO, ANIO, ESTADOCONTRATO,
	IDUNIDADEJECUTORA, FECHACREACIONAUDITORIA, USUARIOCREACIONAUDITORIA, IDCATALOGOESTADODOCUMENTACION
	) 
	VALUES (#{idcontrato,jdbcType=INTEGER},
	#{idgrupodocumento,jdbcType=INTEGER}, #{dniespecialistaejecucion,jdbcType=VARCHAR}, #{nombreespecialista,jdbcType=VARCHAR}, #{fecharecepcionexpediente,jdbcType=TIMESTAMP}, 
    #{nroconsolid,jdbcType=INTEGER}, #{nrocontrato,jdbcType=INTEGER}, #{anio,jdbcType=INTEGER}, #{estadocontrato,jdbcType=INTEGER},
    #{idunidadejecutora,jdbcType=INTEGER}, #{fechacreacionauditoria,jdbcType=TIMESTAMP}, #{usuariocreacionauditoria,jdbcType=VARCHAR}, #{idcatalogoestadodocumentacion,jdbcType=VARCHAR}
	)

mapper.select:
	select 'false' as QUERYID,
	
	from abas.Contrato A1
	
		
			 AND A1.IDCONTRATO = #{idcontrato,jdbcType=INTEGER} 
		
		
			 AND A1.IDUNIDADEJECUTORA = #{idunidadejecutora,jdbcType=INTEGER} 
		
		
			 AND A1.NROITEMS = #{nroitems,jdbcType=SMALLINT} 
		
		
			 AND A1.IDCATALOGOSISTEMAADQUISICION = #{idcatalogosistemaadquisicion,jdbcType=VARCHAR} 
		
		
			 AND A1.IDGRUPODOCUMENTO = #{idgrupodocumento,jdbcType=INTEGER} 
		
		
			 AND A1.IDCATALOGOTIPOCONTRATO = #{idcatalogotipocontrato,jdbcType=VARCHAR} 
		
		
			 AND A1.NROCONTRATO = #{nrocontrato,jdbcType=INTEGER} 
		
		
			 AND A1.SECUENCIACONTRATO = #{secuenciacontrato,jdbcType=INTEGER} 
		
		
			 AND A1.NROCCP LIKE ('%' + #{nroccp,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.IDCATALOGOTIPOBIEN = #{idcatalogotipobien,jdbcType=VARCHAR} 
		
		
			 AND A1.IDPROVEEDOR = #{idproveedor,jdbcType=INTEGER} 
		
		
			 AND A1.ESTADOCONTRATO = #{estadocontrato,jdbcType=INTEGER} 
		
		
			 AND  (A1.FECHAINICIAL)   >=   #{fechainicialini,jdbcType=TIMESTAMP} 
		

		
			 AND (A1.FECHAINICIAL)  <=   #{fechainicialfin,jdbcType=TIMESTAMP} 

		
		
			 AND  (A1.FECHAFIN)   >=   #{fechafinini,jdbcType=TIMESTAMP} 
		

		
			 AND (A1.FECHAFIN)  <=   #{fechafinfin,jdbcType=TIMESTAMP} 

		
		
			 AND A1.MONEDA LIKE ('%' + #{moneda,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.MONTOCONTRATO  >=  #{montocontratoini,jdbcType=DECIMAL} 
		

		
			 AND A1.MONTOCONTRATO  <=   #{montocontratofin,jdbcType=DECIMAL} 

		
		
			 AND A1.MONTOCERTIFICACION  >=  #{montocertificacionini,jdbcType=DECIMAL} 
		

		
			 AND A1.MONTOCERTIFICACION  <=   #{montocertificacionfin,jdbcType=DECIMAL} 

		
		
			 AND  (A1.FECHACONTRATO)   >=   #{fechacontratoini,jdbcType=TIMESTAMP} 
		

		
			 AND (A1.FECHACONTRATO)  <=   #{fechacontratofin,jdbcType=TIMESTAMP} 

		
		
			 AND A1.TIENEPREVISION LIKE ('%' + #{tieneprevision,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.PLAZOEJECUCION = #{plazoejecucion,jdbcType=INTEGER} 
		
		
			 AND A1.CANTIDADARMADAS = #{cantidadarmadas,jdbcType=INTEGER} 
		
		
			 AND  (A1.FECHARECEPCIONEXPEDIENTE)   >=   #{fecharecepcionexpedienteini,jdbcType=TIMESTAMP} 
		

		
			 AND (A1.FECHARECEPCIONEXPEDIENTE)  <=   #{fecharecepcionexpedientefin,jdbcType=TIMESTAMP} 

		
		
			 AND A1.DNIABOGADO LIKE ('%' + #{dniabogado,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.DNIESPECIALISTAEJECUCION LIKE ('%' + #{dniespecialistaejecucion,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.SINADCONTRATO LIKE ('%' + #{sinadcontrato,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.ANIOSINADCONTRATO = #{aniosinadcontrato,jdbcType=INTEGER} 
		
		
			 AND A1.NOMBREABOGADO LIKE ('%' + #{nombreabogado,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.NOMBREESPECIALISTA LIKE ('%' + #{nombreespecialista,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.NUMEROEXPEDIENTE LIKE ('%' + #{numeroexpediente,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.ANIO = #{anio,jdbcType=INTEGER} 
		
		
			 AND A1.NROCONSOLID = #{nroconsolid,jdbcType=INTEGER} 
		
		
			 AND A1.RUTACONTRATO LIKE ('%' + #{rutacontrato,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.IDCATALOGOESTADODOCUMENTACION = #{idcatalogoestadodocumentacion,jdbcType=VARCHAR} 
		
		
			 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 
		

		
			 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 

		
		
			 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')
		
		
			 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 
		

		
			 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 

		
		
			 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')
		
		
			 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')
		

		
			AND A1.IDUNIDADEJECUTORA IN
			   #{var,jdbcType=INTEGER} 
		

		
			AND A1.IDCATALOGOSISTEMAADQUISICION IN
			   #{var,jdbcType=VARCHAR} 
		

		
			AND A1.IDGRUPODOCUMENTO IN
			   #{var,jdbcType=INTEGER} 
		

		
			AND A1.IDCATALOGOTIPOCONTRATO IN
			   #{var,jdbcType=VARCHAR} 
		

		
			AND A1.IDCATALOGOTIPOBIEN IN
			   #{var,jdbcType=VARCHAR} 
		

		
			AND A1.IDCATALOGOESTADODOCUMENTACION IN
			   #{var,jdbcType=VARCHAR} 
		
	

	
		 ORDER BY 
		   ${ord} 
		
			 ${ordenTipo}
