mapper.select:
select 'false' as QUERYID,

from abas.ConvocatoriaProcesoSeleccion A1

where A1.IdConvocatoriaProceso = #{idconvocatoriaproceso,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.ConvocatoriaProcesoSeleccion A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoEstadoConvocatoria = A3.vchregCodigo LEFT JOIN abas.ProcesoSeleccion A5 ON A1.IdProcesoSeleccion = A5.IdProcesoSeleccion where A1.IdConvocatoriaProceso = #{idconvocatoriaproceso,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.ConvocatoriaProcesoSeleccion A1


		 AND A1.IDCONVOCATORIAPROCESO = #{idconvocatoriaproceso,jdbcType=INTEGER} 


		 AND A1.IDPROCESOSELECCION = #{idprocesoseleccion,jdbcType=INTEGER} 


		 AND A1.NROCONVOCATORIA = #{nroconvocatoria,jdbcType=INTEGER} 


		 AND A1.SECUENCIA = #{secuencia,jdbcType=INTEGER} 


		 AND A1.VALORREFERENCIA  >=  #{valorreferenciaini,jdbcType=DECIMAL} 



		 AND A1.VALORREFERENCIA  <=   #{valorreferenciafin,jdbcType=DECIMAL} 



		 AND A1.IDCATALOGOESTADOCONVOCATORIA = #{idcatalogoestadoconvocatoria,jdbcType=VARCHAR} 


		 AND A1.ESTADOCONVOCATORIAITEM = #{estadoconvocatoriaitem,jdbcType=INTEGER} 


		 AND  (A1.FECHAINICIO)   >=   #{fechainicioini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAINICIO)  <=   #{fechainiciofin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHAFIN)   >=   #{fechafinini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAFIN)  <=   #{fechafinfin,jdbcType=TIMESTAMP} 



		 AND A1.PRINCIPAL LIKE ('%' + #{principal,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPROCESOSELECCION IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOESTADOCONVOCATORIA IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.ConvocatoriaProcesoSeleccion A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoEstadoConvocatoria = A3.vchregCodigo
LEFT JOIN abas.ProcesoSeleccion A5 ON A1.IdProcesoSeleccion = A5.IdProcesoSeleccion


