mapper.select:
select 'false' as QUERYID,

from abas.CuadroComparativoItem A1

where A1.IdCuadroComparativoItem = #{idcuadrocomparativoitem,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.CuadroComparativoItem A1
LEFT JOIN abas.CuadroComparativoFuente A3 ON A1.IdCuadroComparativoFuente = A3.IdCuadroComparativoFuente LEFT JOIN abas.DetallePedido A10 ON A1.IdDetallePedido = A10.IdDetallePedido where A1.IdCuadroComparativoItem = #{idcuadrocomparativoitem,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.CuadroComparativoItem A1


		 AND A1.IDCUADROCOMPARATIVOITEM = #{idcuadrocomparativoitem,jdbcType=INTEGER} 


		 AND A1.IDCUADROCOMPARATIVOFUENTE = #{idcuadrocomparativofuente,jdbcType=INTEGER} 


		 AND A1.IDDETALLEPEDIDO = #{iddetallepedido,jdbcType=INTEGER} 


		 AND A1.PRECIOREFERENCIAL  >=  #{precioreferencialini,jdbcType=DECIMAL} 



		 AND A1.PRECIOREFERENCIAL  <=   #{precioreferencialfin,jdbcType=DECIMAL} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCUADROCOMPARATIVOFUENTE IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDDETALLEPEDIDO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.CuadroComparativoItem A1
LEFT JOIN abas.CuadroComparativoFuente A3 ON A1.IdCuadroComparativoFuente = A3.IdCuadroComparativoFuente
LEFT JOIN abas.DetallePedido A10 ON A1.IdDetallePedido = A10.IdDetallePedido


		 AND A1.IDCUADROCOMPARATIVOITEM = #{idcuadrocomparativoitem,jdbcType=INTEGER} 


		 AND A1.IDCUADROCOMPARATIVOFUENTE = #{idcuadrocomparativofuente,jdbcType=INTEGER} 


		 AND A1.IDDETALLEPEDIDO = #{iddetallepedido,jdbcType=INTEGER} 


