mapper.select:
select 'false' as QUERYID,

from abas.DocumentoTecnico A1

where A1.IdDocumentoTecnico = #{iddocumentotecnico,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.DocumentoTecnico A1
LEFT JOIN abas.ComiteProceso A3 ON A1.IdComiteProceso = A3.IdComiteProceso LEFT JOIN abas.gentabla A4 ON A1.IdCatalogoTipoTDR = A4.vchregCodigo LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoTipoDocumentoTecnico = A6.vchregCodigo LEFT JOIN abas.PacProgramado A8 ON A1.IdPacProgramado = A8.IdPacProgramado LEFT JOIN abas.Pedido A12 ON A1.IdPedido = A12.IdPedido where A1.IdDocumentoTecnico = #{iddocumentotecnico,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.DocumentoTecnico A1


		 AND A1.IDDOCUMENTOTECNICO = #{iddocumentotecnico,jdbcType=INTEGER} 


		 AND A1.IDPEDIDO = #{idpedido,jdbcType=INTEGER} 


		 AND A1.IDPACPROGRAMADO = #{idpacprogramado,jdbcType=INTEGER} 


		 AND A1.IDCOMITEPROCESO = #{idcomiteproceso,jdbcType=INTEGER} 


		 AND A1.DENOMINACIONCONTRATACION LIKE ('%' + #{denominacioncontratacion,jdbcType=VARCHAR} + '%')


		 AND A1.FINALIDADPUBLICA LIKE ('%' + #{finalidadpublica,jdbcType=VARCHAR} + '%')


		 AND A1.NROPAC = #{nropac,jdbcType=INTEGER} 


		 AND A1.OBJETOCONTRATACION LIKE ('%' + #{objetocontratacion,jdbcType=VARCHAR} + '%')


		 AND A1.CENTROCOSTO LIKE ('%' + #{centrocosto,jdbcType=VARCHAR} + '%')


		 AND A1.ANTECEDENTES LIKE ('%' + #{antecedentes,jdbcType=VARCHAR} + '%')


		 AND A1.DNIRESPONSABLE LIKE ('%' + #{dniresponsable,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBRERESPONSABLE LIKE ('%' + #{nombreresponsable,jdbcType=VARCHAR} + '%')


		 AND A1.NROANEXORESPONSABLE LIKE ('%' + #{nroanexoresponsable,jdbcType=VARCHAR} + '%')


		 AND A1.CORREORESPONSABLE LIKE ('%' + #{correoresponsable,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOTIPODOCUMENTOTECNICO = #{idcatalogotipodocumentotecnico,jdbcType=VARCHAR} 


		 AND A1.IDCATALOGOTIPOTDR = #{idcatalogotipotdr,jdbcType=VARCHAR} 


		 AND A1.RUTAANEXO LIKE ('%' + #{rutaanexo,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.UBIGEO LIKE ('%' + #{ubigeo,jdbcType=VARCHAR} + '%')


		 AND A1.DIRECCION LIKE ('%' + #{direccion,jdbcType=VARCHAR} + '%')


		 AND A1.NUMEROSINAD = #{numerosinad,jdbcType=INTEGER} 


		 AND A1.ANIOEXPEDIENTE = #{anioexpediente,jdbcType=INTEGER} 


		 AND A1.NUMEROEXPEDIENTE LIKE ('%' + #{numeroexpediente,jdbcType=VARCHAR} + '%')


		 AND A1.TIENECOMITE LIKE ('%' + #{tienecomite,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOITINERARIO LIKE ('%' + #{tipoitinerario,jdbcType=VARCHAR} + '%')


		 AND A1.NROPEDIDOITINERARIO LIKE ('%' + #{nropedidoitinerario,jdbcType=VARCHAR} + '%')


		 AND A1.ANIOPEDIDOITINERARIO = #{aniopedidoitinerario,jdbcType=INTEGER} 


		 AND A1.UEPEDIDOITINERARIO LIKE ('%' + #{uepedidoitinerario,jdbcType=VARCHAR} + '%')


		 AND A1.JUSTIFICACIONPEDIDOITINERARIO LIKE ('%' + #{justificacionpedidoitinerario,jdbcType=VARCHAR} + '%')


		 AND A1.ITINERARIOORIGENREGISTRADO LIKE ('%' + #{itinerarioorigenregistrado,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPEDIDO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDPACPROGRAMADO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCOMITEPROCESO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOTIPODOCUMENTOTECNICO IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOTIPOTDR IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

