mapper.select:
select 'false' as QUERYID,

from abas.EstadosPorEtapaPorDocumento A1

where A1.IdEstadosPorEtapaPorDocumento = #{idestadosporetapapordocumento,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.EstadosPorEtapaPorDocumento A1
LEFT JOIN abas.EstadosPorTipoDocumento A3 ON A1.IdEstadosPorTipoDocumento = A3.IdEstadosPorTipoDocumento LEFT JOIN abas.GrupoDocumento A7 ON A1.IdGrupoDocumento = A7.IdGrupoDocumento LEFT JOIN abas.TipoDocumento A8 ON A1.IdTipoDocumento = A8.IdTipoDocumento where A1.IdEstadosPorEtapaPorDocumento = #{idestadosporetapapordocumento,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.EstadosPorEtapaPorDocumento A1


		 AND A1.IDESTADOSPORETAPAPORDOCUMENTO = #{idestadosporetapapordocumento,jdbcType=INTEGER} 


		 AND A1.IDESTADOSPORTIPODOCUMENTO = #{idestadosportipodocumento,jdbcType=INTEGER} 


		 AND A1.IDGRUPODOCUMENTO = #{idgrupodocumento,jdbcType=INTEGER} 


		 AND A1.NRODOCUMENTO = #{nrodocumento,jdbcType=INTEGER} 


		 AND  (A1.FECHAINGRESO)   >=   #{fechaingresoini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAINGRESO)  <=   #{fechaingresofin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHASALIDA)   >=   #{fechasalidaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHASALIDA)  <=   #{fechasalidafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIORESPONSABLEINGRESO = #{usuarioresponsableingreso,jdbcType=BIGINT} 


		 AND A1.USUARIORESPONSABLESALIDA = #{usuarioresponsablesalida,jdbcType=BIGINT} 


		 AND A1.IDTIPODOCUMENTO = #{idtipodocumento,jdbcType=INTEGER} 


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDESTADOSPORTIPODOCUMENTO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDGRUPODOCUMENTO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDTIPODOCUMENTO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.EstadosPorEtapaPorDocumento A1
