LEFT JOIN abas.GrupoDocumento A7 ON A1.IdGrupoDocumento = A7.IdGrupoDocumento
LEFT JOIN abas.TipoDocumento A8 ON A1.IdTipoDocumento = A8.IdTipoDocumento


