mapper.select:
select 'false' as QUERYID,

from abas.genparametro A1

where A1.vchparamCodigo = #{vchparamcodigo,jdbcType=VARCHAR}

select 'false' as QUERYID,
from abas.genparametro A1
LEFT JOIN abas.gentabla A3 ON A1.vchparamgrupo = A3.vchregCodigo where A1.vchparamCodigo = #{vchparamcodigo,jdbcType=VARCHAR}

mapper.insert:
insert into abas.genparametro (
VCHPARAMCODIGO, VCHPARAMVALOR, VCHPARAMDESCRI, VCHPARAMDESCRIADI, 
VCHPARAMGRUPO, ESTADOAUDITORIA, USUARIOCREACIONAUDITORIA, USUARIOMODIFICACIONAUDITORIA, 
FECHACREACIONAUDITORIA, FECHAMODIFICACIONAUDITORIA, EQUIPOAUDITORIA, PROGRAMAAUDITORIA 
) 
VALUES (
#{vchparamcodigo,jdbcType=VARCHAR}, #{vchparamvalor,jdbcType=VARCHAR}, #{vchparamdescri,jdbcType=VARCHAR}, #{vchparamdescriadi,jdbcType=VARCHAR}, 
#{vchparamgrupo,jdbcType=VARCHAR}, #{estadoauditoria,jdbcType=VARCHAR}, #{usuariocreacionauditoria,jdbcType=VARCHAR}, #{usuariomodificacionauditoria,jdbcType=VARCHAR}, 
#{fechacreacionauditoria,jdbcType=TIMESTAMP}, #{fechamodificacionauditoria,jdbcType=TIMESTAMP}, #{equipoauditoria,jdbcType=VARCHAR}, #{programaauditoria,jdbcType=VARCHAR} 
)

mapper.select:
select 'false' as QUERYID,

from abas.genparametro A1


		 AND A1.VCHPARAMCODIGO LIKE ('%' + #{vchparamcodigo,jdbcType=VARCHAR} + '%')


		 AND A1.VCHPARAMVALOR LIKE ('%' + #{vchparamvalor,jdbcType=VARCHAR} + '%')


		 AND A1.VCHPARAMDESCRI LIKE ('%' + #{vchparamdescri,jdbcType=VARCHAR} + '%')


		 AND A1.VCHPARAMDESCRIADI LIKE ('%' + #{vchparamdescriadi,jdbcType=VARCHAR} + '%')


		 AND A1.VCHPARAMGRUPO = #{vchparamgrupo,jdbcType=VARCHAR} 


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')



		AND A1.VCHPARAMGRUPO IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

