mapper.select:
select 'false' as QUERYID,

from abas.gentabla A1

where A1.vchregCodigo = #{vchregcodigo,jdbcType=VARCHAR}

select 'false' as QUERYID,
from abas.gentabla A1
LEFT JOIN abas.gentipo A3 ON A1.vchtipCodigo = A3.vchtipCodigo where A1.vchregCodigo = #{vchregcodigo,jdbcType=VARCHAR}

select 'false' as QUERYID,

from abas.gentabla A1


		 AND A1.VCHREGCODIGO LIKE ('%' + #{vchregcodigo,jdbcType=VARCHAR} + '%')


		 AND A1.VCHREGDESCRI LIKE ('%' + #{vchregdescri,jdbcType=VARCHAR} + '%')


		 AND A1.VCHREGABREVIADO LIKE ('%' + #{vchregabreviado,jdbcType=VARCHAR} + '%')


		 AND A1.VCHTIPCODIGO = #{vchtipcodigo,jdbcType=VARCHAR} 


		 AND A1.INTREGORDEN = #{intregorden,jdbcType=INTEGER} 


		 AND A1.VCHTABOTRO1 LIKE ('%' + #{vchtabotro1,jdbcType=VARCHAR} + '%')


		 AND A1.VCHTABOTRO2 LIKE ('%' + #{vchtabotro2,jdbcType=VARCHAR} + '%')


		 AND A1.VCHTABOTRO3 LIKE ('%' + #{vchtabotro3,jdbcType=VARCHAR} + '%')


		 AND A1.VCHTABOTRO4 LIKE ('%' + #{vchtabotro4,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.INTGANEMPID = #{intganempid,jdbcType=INTEGER} 



		AND A1.VCHTIPCODIGO IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.gentabla A1
LEFT JOIN abas.gentipo A3 ON A1.vchtipCodigo = A3.vchtipCodigo


