mapper.select:
select 'false' as QUERYID,

from abas.MiembroComitePorProceso A1

where A1.IdMiembroComiteProceso = #{idmiembrocomiteproceso,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.MiembroComitePorProceso A1
LEFT JOIN abas.ComiteProceso A3 ON A1.IdComiteProceso = A3.IdComiteProceso LEFT JOIN abas.gentabla A4 ON A1.IdCatalogoTipoMiembro = A4.vchregCodigo LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoEstadoMiembroComite = A6.vchregCodigo LEFT JOIN abas.Persona A8 ON A1.IdPersona = A8.IdPersona where A1.IdMiembroComiteProceso = #{idmiembrocomiteproceso,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.MiembroComitePorProceso A1


		 AND A1.IDMIEMBROCOMITEPROCESO = #{idmiembrocomiteproceso,jdbcType=INTEGER} 


		 AND A1.IDCOMITEPROCESO = #{idcomiteproceso,jdbcType=INTEGER} 


		 AND A1.IDPERSONA = #{idpersona,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOTIPOMIEMBRO = #{idcatalogotipomiembro,jdbcType=VARCHAR} 


		 AND A1.CORREO LIKE ('%' + #{correo,jdbcType=VARCHAR} + '%')


		 AND A1.TELEFONO LIKE ('%' + #{telefono,jdbcType=VARCHAR} + '%')


		 AND A1.ANEXO LIKE ('%' + #{anexo,jdbcType=VARCHAR} + '%')


		 AND A1.CELULAR LIKE ('%' + #{celular,jdbcType=VARCHAR} + '%')


		 AND A1.ESNOTIFICADO LIKE ('%' + #{esnotificado,jdbcType=VARCHAR} + '%')


		 AND A1.CODIGOOFICINAUSUARIA LIKE ('%' + #{codigooficinausuaria,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREOFICINAUSUARIA LIKE ('%' + #{nombreoficinausuaria,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOESTADOMIEMBROCOMITE = #{idcatalogoestadomiembrocomite,jdbcType=VARCHAR} 


		 AND A1.ESESPECIALISTAASIGNADO LIKE ('%' + #{esespecialistaasignado,jdbcType=VARCHAR} + '%')


		 AND A1.FECHACREACIONAUDITORIA  >=  #{fechacreacionauditoriaini,jdbcType=DATE} 



		 AND A1.FECHACREACIONAUDITORIA  <=   #{fechacreacionauditoriafin,jdbcType=DATE} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.FECHAMODIFICACIONAUDITORIA  >=  #{fechamodificacionauditoriaini,jdbcType=DATE} 



		 AND A1.FECHAMODIFICACIONAUDITORIA  <=   #{fechamodificacionauditoriafin,jdbcType=DATE} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCOMITEPROCESO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDPERSONA IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOTIPOMIEMBRO IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOESTADOMIEMBROCOMITE IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

