mapper.select:
select 'false' as QUERYID,

from abas.Orden A1

where A1.IdOrden = #{idorden,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.Orden A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoTipoBien = A3.vchregCodigo LEFT JOIN abas.GrupoDocumento A5 ON A1.IdGrupoDocumento = A5.IdGrupoDocumento LEFT JOIN abas.PacConsolidado A6 ON A1.IdPacConsolidado = A6.IdPacConsolidado LEFT JOIN abas.UnidadEjecutora A16 ON A1.IdUnidadEjecutora = A16.IdUnidadEjecutora where A1.IdOrden = #{idorden,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.Orden A1


		 AND A1.IDORDEN = #{idorden,jdbcType=INTEGER} 


		 AND A1.IDPACCONSOLIDADO = #{idpacconsolidado,jdbcType=INTEGER} 


		 AND A1.IDGRUPODOCUMENTO = #{idgrupodocumento,jdbcType=INTEGER} 


		 AND A1.NROORDEN LIKE ('%' + #{nroorden,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAORDEN)   >=   #{fechaordenini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAORDEN)  <=   #{fechaordenfin,jdbcType=TIMESTAMP} 



		 AND A1.ANIOORDEN = #{anioorden,jdbcType=INTEGER} 


		 AND A1.ESTADOORDEN = #{estadoorden,jdbcType=SMALLINT} 


		 AND A1.NROEXPEDIENTESIAF LIKE ('%' + #{nroexpedientesiaf,jdbcType=VARCHAR} + '%')


		 AND A1.MONEDA LIKE ('%' + #{moneda,jdbcType=VARCHAR} + '%')


		 AND A1.MONTOORDEN  >=  #{montoordenini,jdbcType=DECIMAL} 



		 AND A1.MONTOORDEN  <=   #{montoordenfin,jdbcType=DECIMAL} 



		 AND A1.NROPROCESO LIKE ('%' + #{nroproceso,jdbcType=VARCHAR} + '%')


		 AND A1.NROCONTRATO LIKE ('%' + #{nrocontrato,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOEXPEDIENTESIAF = #{estadoexpedientesiaf,jdbcType=SMALLINT} 


		 AND  (A1.FECHAINICIOPRESTACION)   >=   #{fechainicioprestacionini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAINICIOPRESTACION)  <=   #{fechainicioprestacionfin,jdbcType=TIMESTAMP} 



		 AND A1.IDCATALOGOTIPOBIEN = #{idcatalogotipobien,jdbcType=VARCHAR} 


		 AND  (A1.FECHAFINPRESTACION)   >=   #{fechafinprestacionini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAFINPRESTACION)  <=   #{fechafinprestacionfin,jdbcType=TIMESTAMP} 



		 AND A1.ANIO = #{anio,jdbcType=INTEGER} 


		 AND A1.IDUNIDADEJECUTORA = #{idunidadejecutora,jdbcType=INTEGER} 


		 AND A1.PLAZOEJECUCION = #{plazoejecucion,jdbcType=INTEGER} 


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPACCONSOLIDADO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDGRUPODOCUMENTO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOTIPOBIEN IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDUNIDADEJECUTORA IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

