mapper.select:
select 'false' as QUERYID,

from abas.PacProgramado A1

where A1.IdPacProgramado = #{idpacprogramado,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.PacProgramado A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoTipoBien = A3.vchregCodigo LEFT JOIN abas.UnidadEjecutora A7 ON A1.IdUnidadEjecutora = A7.IdUnidadEjecutora where A1.IdPacProgramado = #{idpacprogramado,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.PacProgramado A1


		 AND A1.IDPACPROGRAMADO = #{idpacprogramado,jdbcType=INTEGER} 


		 AND A1.ANIO = #{anio,jdbcType=INTEGER} 


		 AND A1.IDUNIDADEJECUTORA = #{idunidadejecutora,jdbcType=INTEGER} 


		 AND A1.NROCONSOLID LIKE ('%' + #{nroconsolid,jdbcType=VARCHAR} + '%')


		 AND A1.ESPECIFICACIONTECNICA LIKE ('%' + #{especificaciontecnica,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOTIPOBIEN = #{idcatalogotipobien,jdbcType=VARCHAR} 


		 AND A1.MONTOPAO  >=  #{montopaoini,jdbcType=DECIMAL} 



		 AND A1.MONTOPAO  <=   #{montopaofin,jdbcType=DECIMAL} 



		 AND A1.CODIGOCENTROCOSTO LIKE ('%' + #{codigocentrocosto,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREDEPENDENCIA LIKE ('%' + #{nombredependencia,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADO = #{estado,jdbcType=INTEGER} 


		 AND A1.TIPODOCUMENTOSINAD LIKE ('%' + #{tipodocumentosinad,jdbcType=VARCHAR} + '%')


		 AND A1.NUMERODOCUMENTOSINAD LIKE ('%' + #{numerodocumentosinad,jdbcType=VARCHAR} + '%')


		 AND A1.NUMEROSINAD = #{numerosinad,jdbcType=INTEGER} 


		 AND A1.ANIOSINAD = #{aniosinad,jdbcType=INTEGER} 


		 AND A1.NUMEROEXPEDIENTESINAD LIKE ('%' + #{numeroexpedientesinad,jdbcType=VARCHAR} + '%')


		 AND A1.FECHACREACIONAUDITORIA  >=  #{fechacreacionauditoriaini,jdbcType=DATE} 



		 AND A1.FECHACREACIONAUDITORIA  <=   #{fechacreacionauditoriafin,jdbcType=DATE} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.FECHAMODIFICACIONAUDITORIA  >=  #{fechamodificacionauditoriaini,jdbcType=DATE} 



		 AND A1.FECHAMODIFICACIONAUDITORIA  <=   #{fechamodificacionauditoriafin,jdbcType=DATE} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDUNIDADEJECUTORA IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOTIPOBIEN IN
		   #{var,jdbcType=VARCHAR} 





	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

