select 'false' as QUERYID,

from abas.Pedido A1

where A1.IdPedido = #{idpedido,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.Pedido A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoTipoBien = A3.vchregCodigo LEFT JOIN abas.gentabla A5 ON A1.IdCatalogoTipoNecesidad = A5.vchregCodigo LEFT JOIN abas.Periodo A7 ON A1.IdPeriodo = A7.IdPeriodo LEFT JOIN abas.UnidadEjecutora A8 ON A1.IdUnidadEjecutora = A8.IdUnidadEjecutora LEFT JOIN abas.VCentroCosto A9 ON A1.CodigoCentroCosto = A9.CodigoCentroCosto LEFT JOIN abas.visPedido A10 ON A1.IdVPedido = A10.IdVPedido where A1.IdPedido = #{idpedido,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.Pedido A1


		 AND A1.IDPEDIDO = #{idpedido,jdbcType=INTEGER} 


		 AND A1.NROPEDIDO LIKE ('%' + #{nropedido,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOTIPOBIEN = #{idcatalogotipobien,jdbcType=VARCHAR} 


		 AND A1.IDPERIODO = #{idperiodo,jdbcType=INTEGER} 


		 AND A1.CODIGOCENTROCOSTO = #{codigocentrocosto,jdbcType=VARCHAR} 


		 AND A1.ANIO = #{anio,jdbcType=INTEGER} 


		 AND A1.NOMBREDEPENDENCIA LIKE ('%' + #{nombredependencia,jdbcType=VARCHAR} + '%')


		 AND A1.DESCRIPCIONPEDIDOSIGA LIKE ('%' + #{descripcionpedidosiga,jdbcType=VARCHAR} + '%')


		 AND A1.MONTOPEDIDO  >=  #{montopedidoini,jdbcType=DECIMAL} 



		 AND A1.MONTOPEDIDO  <=   #{montopedidofin,jdbcType=DECIMAL} 



		 AND A1.ESTADOPEDIDO = #{estadopedido,jdbcType=INTEGER} 


		 AND A1.NUMEROSINAD = #{numerosinad,jdbcType=INTEGER} 


		 AND A1.ANIOSINAD = #{aniosinad,jdbcType=INTEGER} 


		 AND A1.NUMEROEXPEDIENTESINAD LIKE ('%' + #{numeroexpedientesinad,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAPEDIDO)   >=   #{fechapedidoini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAPEDIDO)  <=   #{fechapedidofin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOTIPONECESIDAD = #{idcatalogotiponecesidad,jdbcType=VARCHAR} 


		 AND A1.IDUNIDADEJECUTORA = #{idunidadejecutora,jdbcType=INTEGER} 


		 AND A1.TIPODOCUMENTOSINAD LIKE ('%' + #{tipodocumentosinad,jdbcType=VARCHAR} + '%')


		 AND A1.NUMERODOCUMENTOSINAD LIKE ('%' + #{numerodocumentosinad,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOSINAD LIKE ('%' + #{tiposinad,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.IDVPEDIDO = #{idvpedido,jdbcType=INTEGER} 


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCATALOGOTIPOBIEN IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDPERIODO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.CODIGOCENTROCOSTO IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOTIPONECESIDAD IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDUNIDADEJECUTORA IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDVPEDIDO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

