mapper.select:
select 'false' as QUERYID,

from abas.PrevisionPorContrato A1

where A1.IdPrevisionPorContrato = #{idprevisionporcontrato,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.PrevisionPorContrato A1
LEFT JOIN abas.Contrato A3 ON A1.IdContrato = A3.IdContrato LEFT JOIN abas.PrevisionPresupuestal A10 ON A1.IdPrevisionPresupuestal = A10.IdPrevisionPresupuestal where A1.IdPrevisionPorContrato = #{idprevisionporcontrato,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.PrevisionPorContrato A1


		 AND A1.IDPREVISIONPORCONTRATO = #{idprevisionporcontrato,jdbcType=INTEGER} 


		 AND A1.IDPREVISIONPRESUPUESTAL = #{idprevisionpresupuestal,jdbcType=INTEGER} 


		 AND A1.IDCONTRATO = #{idcontrato,jdbcType=INTEGER} 


		 AND A1.MONTOPREVISIOCONTRATO  >=  #{montoprevisiocontratoini,jdbcType=DECIMAL} 



		 AND A1.MONTOPREVISIOCONTRATO  <=   #{montoprevisiocontratofin,jdbcType=DECIMAL} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPREVISIONPRESUPUESTAL IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCONTRATO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.PrevisionPorContrato A1
LEFT JOIN abas.Contrato A3 ON A1.IdContrato = A3.IdContrato
LEFT JOIN abas.PrevisionPresupuestal A10 ON A1.IdPrevisionPresupuestal = A10.IdPrevisionPresupuestal


		 AND A1.IDPREVISIONPORCONTRATO = #{idprevisionporcontrato,jdbcType=INTEGER} 


