mapper.select:
select 'false' as QUERYID,

from abas.ResultadoProcesoSeleccion A1

where A1.IdResultadoProceso = #{idresultadoproceso,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.ResultadoProcesoSeleccion A1
LEFT JOIN abas.ConvocatoriaProcesoSeleccion A3 ON A1.IdConvocatoriaProceso = A3.IdConvocatoriaProceso LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoEstadoResultado = A6.vchregCodigo LEFT JOIN abas.ResultadoProcesoSeleccion A8 ON A1.IdResultadoProcesoPadre = A8.IdResultadoProceso where A1.IdResultadoProceso = #{idresultadoproceso,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.ResultadoProcesoSeleccion A1


		 AND A1.IDRESULTADOPROCESO = #{idresultadoproceso,jdbcType=INTEGER} 


		 AND A1.IDCONVOCATORIAPROCESO = #{idconvocatoriaproceso,jdbcType=INTEGER} 


		 AND A1.IDRESULTADOPROCESOPADRE = #{idresultadoprocesopadre,jdbcType=INTEGER} 


		 AND A1.NROITEM LIKE ('%' + #{nroitem,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREITEM LIKE ('%' + #{nombreitem,jdbcType=VARCHAR} + '%')


		 AND A1.NRORUC LIKE ('%' + #{nroruc,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREPROVEEDOR LIKE ('%' + #{nombreproveedor,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOESTADORESULTADO = #{idcatalogoestadoresultado,jdbcType=VARCHAR} 


		 AND A1.ESTADOPROCESOITEM = #{estadoprocesoitem,jdbcType=INTEGER} 


		 AND A1.VALORREFERENCIAL  >=  #{valorreferencialini,jdbcType=DECIMAL} 



		 AND A1.VALORREFERENCIAL  <=   #{valorreferencialfin,jdbcType=DECIMAL} 



		 AND A1.MONTOADJUDICADO  >=  #{montoadjudicadoini,jdbcType=DECIMAL} 



		 AND A1.MONTOADJUDICADO  <=   #{montoadjudicadofin,jdbcType=DECIMAL} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCONVOCATORIAPROCESO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDRESULTADOPROCESOPADRE IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOESTADORESULTADO IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.ResultadoProcesoSeleccion A1
LEFT JOIN abas.ConvocatoriaProcesoSeleccion A3 ON A1.IdConvocatoriaProceso = A3.IdConvocatoriaProceso
LEFT JOIN abas.gentabla A6 ON A1.IdCatalogoEstadoResultado = A6.vchregCodigo
LEFT JOIN abas.ResultadoProcesoSeleccion A8 ON A1.IdResultadoProcesoPadre = A8.IdResultadoProceso


		 AND A1.IDRESULTADOPROCESO = #{idresultadoproceso,jdbcType=INTEGER} 


		 AND A1.IDCONVOCATORIAPROCESO = #{idconvocatoriaproceso,jdbcType=INTEGER} 


		 AND A1.IDRESULTADOPROCESOPADRE = #{idresultadoprocesopadre,jdbcType=INTEGER} 


		 AND A1.NROITEM LIKE ('%' + #{nroitem,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREITEM LIKE ('%' + #{nombreitem,jdbcType=VARCHAR} + '%')


		 AND A1.NRORUC LIKE ('%' + #{nroruc,jdbcType=VARCHAR} + '%')


		 AND A1.NOMBREPROVEEDOR LIKE ('%' + #{nombreproveedor,jdbcType=VARCHAR} + '%')


		 AND A1.IDCATALOGOESTADORESULTADO = #{idcatalogoestadoresultado,jdbcType=VARCHAR} 


		 AND A1.ESTADOPROCESOITEM = #{estadoprocesoitem,jdbcType=INTEGER} 


		 AND A1.VALORREFERENCIAL  >=  #{valorreferencialini,jdbcType=DECIMAL} 



		 AND A1.VALORREFERENCIAL  <=   #{valorreferencialfin,jdbcType=DECIMAL} 



		 AND A1.MONTOADJUDICADO  >=  #{montoadjudicadoini,jdbcType=DECIMAL} 



		 AND A1.MONTOADJUDICADO  <=   #{montoadjudicadofin,jdbcType=DECIMAL} 



		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCONVOCATORIAPROCESO IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDRESULTADOPROCESOPADRE IN
		   #{var,jdbcType=INTEGER} 



		AND A1.IDCATALOGOESTADORESULTADO IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}
