mapper.select:
select 'false' as QUERYID,

from abas.SeccionesDocumentoTecnico A1

where A1.IdSeccionesDocumentoTecnico = #{idseccionesdocumentotecnico,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.SeccionesDocumentoTecnico A1
LEFT JOIN abas.gentabla A3 ON A1.IdCatalogoTipoDocumentoTecnico = A3.vchregCodigo LEFT JOIN abas.gentabla A5 ON A1.IdCatalogoTipoTDR = A5.vchregCodigo where A1.IdSeccionesDocumentoTecnico = #{idseccionesdocumentotecnico,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.SeccionesDocumentoTecnico A1


		 AND A1.IDSECCIONESDOCUMENTOTECNICO = #{idseccionesdocumentotecnico,jdbcType=INTEGER} 


		 AND A1.IDCATALOGOTIPODOCUMENTOTECNICO = #{idcatalogotipodocumentotecnico,jdbcType=VARCHAR} 


		 AND A1.IDCATALOGOTIPOTDR = #{idcatalogotipotdr,jdbcType=VARCHAR} 


		 AND A1.DESCRIPCIONSECCION LIKE ('%' + #{descripcionseccion,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDCATALOGOTIPODOCUMENTOTECNICO IN
		   #{var,jdbcType=VARCHAR} 



		AND A1.IDCATALOGOTIPOTDR IN
		   #{var,jdbcType=VARCHAR} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

select 'false' as QUERYID,

from abas.SeccionesDocumentoTecnico A1
