mapper.select:
select 'false' as QUERYID,

from abas.SinadPorPacConsolidado A1

where A1.IdSinad = #{idsinad,jdbcType=INTEGER}

select 'false' as QUERYID,
from abas.SinadPorPacConsolidado A1
LEFT JOIN abas.PacConsolidado A3 ON A1.IdPacConsolidado = A3.IdPacConsolidado where A1.IdSinad = #{idsinad,jdbcType=INTEGER}

select 'false' as QUERYID,

from abas.SinadPorPacConsolidado A1


		 AND A1.IDSINAD = #{idsinad,jdbcType=INTEGER} 


		 AND A1.IDPACCONSOLIDADO = #{idpacconsolidado,jdbcType=INTEGER} 


		 AND A1.NUMEROSINAD = #{numerosinad,jdbcType=INTEGER} 


		 AND A1.NUMEROEXPEDIENTE LIKE ('%' + #{numeroexpediente,jdbcType=VARCHAR} + '%')


		 AND A1.ANIOEXPEDIENTE = #{anioexpediente,jdbcType=INTEGER} 


		 AND A1.CODIGOSEDEEXPEDIENTE = #{codigosedeexpediente,jdbcType=INTEGER} 


		 AND A1.DESCRIPCIONTIPODOCUMENTO LIKE ('%' + #{descripciontipodocumento,jdbcType=VARCHAR} + '%')


		 AND A1.DESCRIPCIONOFICINAEXPEDIENTE LIKE ('%' + #{descripcionoficinaexpediente,jdbcType=VARCHAR} + '%')


		 AND A1.NUMERODOCUMENTO LIKE ('%' + #{numerodocumento,jdbcType=VARCHAR} + '%')


		 AND A1.ESPRINCIPAL LIKE ('%' + #{esprincipal,jdbcType=VARCHAR} + '%')


		 AND A1.ESNUEVOASIGNADO LIKE ('%' + #{esnuevoasignado,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')



		AND A1.IDPACCONSOLIDADO IN
		   #{var,jdbcType=INTEGER} 




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

