select 'false' as QUERYID,

from abas.visSigaPaacConsolidado A1


		 AND A1.VISIDSIGAPAACCONSOLIDADO = #{visidsigapaacconsolidado,jdbcType=INTEGER} 


		 AND A1.CODIGOUNIDADEJECUTORA LIKE ('%' + #{codigounidadejecutora,jdbcType=VARCHAR} + '%')


		 AND A1.ANIO  >=  #{anioini,jdbcType=DECIMAL} 



		 AND A1.ANIO  <=   #{aniofin,jdbcType=DECIMAL} 



		 AND A1.SECEJEC  >=  #{secejecini,jdbcType=DECIMAL} 



		 AND A1.SECEJEC  <=   #{secejecfin,jdbcType=DECIMAL} 



		 AND A1.TIPOCONSOLID LIKE ('%' + #{tipoconsolid,jdbcType=VARCHAR} + '%')


		 AND A1.NROCONSOLID  >=  #{nroconsolidini,jdbcType=DECIMAL} 



		 AND A1.NROCONSOLID  <=   #{nroconsolidfin,jdbcType=DECIMAL} 



		 AND A1.ESTADOPAAC LIKE ('%' + #{estadopaac,jdbcType=VARCHAR} + '%')


		 AND A1.NROCERTIFICA  >=  #{nrocertificaini,jdbcType=DECIMAL} 



		 AND A1.NROCERTIFICA  <=   #{nrocertificafin,jdbcType=DECIMAL} 



		 AND A1.MODALCOMPRA LIKE ('%' + #{modalcompra,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACONS)   >=   #{fechaconsini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACONS)  <=   #{fechaconsfin,jdbcType=TIMESTAMP} 



		 AND A1.NROPAAC LIKE ('%' + #{nropaac,jdbcType=VARCHAR} + '%')


		 AND A1.MESINICIAL LIKE ('%' + #{mesinicial,jdbcType=VARCHAR} + '%')


		 AND A1.MESFINAL LIKE ('%' + #{mesfinal,jdbcType=VARCHAR} + '%')


		 AND A1.MESPROPUESTO LIKE ('%' + #{mespropuesto,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOBIEN LIKE ('%' + #{tipobien,jdbcType=VARCHAR} + '%')


		 AND A1.FLAGPAAC LIKE ('%' + #{flagpaac,jdbcType=VARCHAR} + '%')


		 AND A1.TIPOPROCESO LIKE ('%' + #{tipoproceso,jdbcType=VARCHAR} + '%')


		 AND A1.DESCRIPCIONTIPOPROCESO LIKE ('%' + #{descripciontipoproceso,jdbcType=VARCHAR} + '%')


		 AND A1.OBJETO LIKE ('%' + #{objeto,jdbcType=VARCHAR} + '%')


		 AND A1.ESPECIFICACIONTECNICAS LIKE ('%' + #{especificaciontecnicas,jdbcType=VARCHAR} + '%')


		 AND A1.VALORMONEDA  >=  #{valormonedaini,jdbcType=DECIMAL} 



		 AND A1.VALORMONEDA  <=   #{valormonedafin,jdbcType=DECIMAL} 



		 AND A1.VALORPLAN  >=  #{valorplanini,jdbcType=DECIMAL} 



		 AND A1.VALORPLAN  <=   #{valorplanfin,jdbcType=DECIMAL} 



		 AND A1.PAIS LIKE ('%' + #{pais,jdbcType=VARCHAR} + '%')


		 AND A1.DEPARTAMENTO LIKE ('%' + #{departamento,jdbcType=VARCHAR} + '%')


		 AND A1.PROVINCIA LIKE ('%' + #{provincia,jdbcType=VARCHAR} + '%')


		 AND A1.DISTRITO LIKE ('%' + #{distrito,jdbcType=VARCHAR} + '%')


		 AND A1.EXPSIGA  >=  #{expsigaini,jdbcType=DECIMAL} 



		 AND A1.EXPSIGA  <=   #{expsigafin,jdbcType=DECIMAL} 



		 AND A1.EXPSIAF  >=  #{expsiafini,jdbcType=DECIMAL} 



		 AND A1.EXPSIAF  <=   #{expsiaffin,jdbcType=DECIMAL} 



		 AND A1.EXPSIAFTIPOEJEC LIKE ('%' + #{expsiaftipoejec,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHATIPOEJEC)   >=   #{fechatipoejecini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHATIPOEJEC)  <=   #{fechatipoejecfin,jdbcType=TIMESTAMP} 



		 AND A1.MODALIDADCSC LIKE ('%' + #{modalidadcsc,jdbcType=VARCHAR} + '%')


		 AND A1.CODSNIP LIKE ('%' + #{codsnip,jdbcType=VARCHAR} + '%')


		 AND A1.DOCVIASNIP LIKE ('%' + #{docviasnip,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAREG)   >=   #{fecharegini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAREG)  <=   #{fecharegfin,jdbcType=TIMESTAMP} 



		 AND  (A1.FECHAMOD)   >=   #{fechamodini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMOD)  <=   #{fechamodfin,jdbcType=TIMESTAMP} 



		 AND A1.TIPOCONTRATACION LIKE ('%' + #{tipocontratacion,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHACREACIONAUDITORIA)   >=   #{fechacreacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHACREACIONAUDITORIA)  <=   #{fechacreacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOCREACIONAUDITORIA LIKE ('%' + #{usuariocreacionauditoria,jdbcType=VARCHAR} + '%')


		 AND  (A1.FECHAMODIFICACIONAUDITORIA)   >=   #{fechamodificacionauditoriaini,jdbcType=TIMESTAMP} 



		 AND (A1.FECHAMODIFICACIONAUDITORIA)  <=   #{fechamodificacionauditoriafin,jdbcType=TIMESTAMP} 



		 AND A1.USUARIOMODIFICACIONAUDITORIA LIKE ('%' + #{usuariomodificacionauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.EQUIPOAUDITORIA LIKE ('%' + #{equipoauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.PROGRAMAAUDITORIA LIKE ('%' + #{programaauditoria,jdbcType=VARCHAR} + '%')


		 AND A1.ESTADOAUDITORIA LIKE ('%' + #{estadoauditoria,jdbcType=VARCHAR} + '%')




	 ORDER BY 
	   ${ord} 

		 ${ordenTipo}

